mapper.select:
select * from kouji where delFlgkouji="0"

   and kjNo  like "%${kjNo}%"


   and person = #{person}

select * from kouji  where delFlgkouji="0"

        and  person = #{person}


        and status = #{status}

select * from kouji where delFlgkouji="0"

        and person=#{username}

ORDER BY bgnYmd DESC limit 10

select * from kouji where delFlgkouji="0"

        and  person = #{person}


       and  status = #{status}

ORDER BY bgnYmd DESC limit 10

select * from kouji where delFlgkouji="0"

       and person=#{username}

ORDER BY updDate DESC limit 10

select * from kouji where delFlgkouji="0"

        and person = #{person}


        and status = #{status}

ORDER BY updDate DESC limit 10

mapper.update:
update kouji
set delFlgkouji=#{delFlgkouji}
WHERE id=#{id}

mapper.select:
select * from kouji
where location = #{location} and delFlgkouji="0"
ORDER BY  bgnYmd DESC;
